import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function ProductOverview() {
    const { id } = useParams(); // Get the product ID from the URL
    const [product, setProduct] = useState(null); // State to store product data
    const [selectedSize, setSelectedSize] = useState(""); // State for selected size
    const [loading, setLoading] = useState(true); // State for loading

    // Fetch product data by ID when the component mounts
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                setLoading(true); // Set loading to true before fetching data
                const response = await fetch(`http://localhost:5000/api/casual/${id}`);
                if (!response.ok) {
                    throw new Error("Failed to fetch product");
                }
                const data = await response.json();
                setProduct(data); // Set the product data after fetching
                setSelectedSize(data.sizes[0] || ""); // Set the default size if available
            } catch (err) {
                console.error("Error fetching product:", err);
            } finally {
                setLoading(false); // Set loading to false after the fetch is complete
            }
        };

        fetchProduct(); // Call the fetch function
    }, [id]); // Run this effect whenever the ID changes (e.g., navigation to a different product)

    // Show a loading message while the product data is being fetched
    if (loading) {
        return <div>Loading...</div>;
    }

    // If product data is not available, show an error message
    if (!product) {
        return <div>Product not found</div>;
    }

    return (
        <div className="bg-white">
            <div className="pt-6">
                {/* Product Image */}
                <div className="mx-auto mt-6 max-w-xl sm:px-6 lg:px-8">
                    <img
                        alt={product.name}
                        src={product.imageSrc}
                        className="w-full max-h-80 rounded-lg object-cover"
                    />
                </div>

                {/* Product Info */}
                <div className="mx-auto max-w-2xl px-4 pb-16 pt-10 sm:px-6 lg:px-8">
                    <h1 className="text-2xl font-bold tracking-tight text-gray-900 sm:text-3xl">
                        {product.name}
                    </h1>
                    <p className="text-3xl tracking-tight text-gray-900">{product.price}</p>
                    <p className="mt-4 text-gray-700">{product.description}</p>

                    {/* Size Dropdown */}
                    <div className="mt-6">
                        <h3 className="text-sm font-medium text-gray-900">Size</h3>
                        <select
                            value={selectedSize}
                            onChange={(e) => setSelectedSize(e.target.value)}
                            className="mt-2 block w-32 rounded-md border-gray-300 shadow-sm focus:border-indigo-500"
                        >
                            {product.sizes && product.sizes.length > 0 ? (
                                product.sizes.map((size) => (
                                    <option key={size} value={size}>
                                        {size}
                                    </option>
                                ))
                            ) : (
                                <option>-</option>
                            )}
                        </select>
                    </div>

                    {/* Add to Cart Button */}
                    <button
                        type="button"
                        className="mt-10 w-full rounded-md bg-gray-900 px-8 py-3 text-white font-medium hover:bg-gray-800"
                    >
                        Add to Bag
                    </button>
                </div>
            </div>
        </div>
    );
}
