import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Casual() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/product/${id}`);
        if (!response.ok) throw new Error("Failed to fetch product");

        const data = await response.json();
        console.log("Fetched Product:", data); // Debug log
        setProduct(data);

        // Safely handle sizes
        setSelectedSize(data.sizes && data.sizes.length > 0 ? data.sizes[0] : "");
      } catch (err) {
        console.error("Error fetching product:", err);
      }
    };

    fetchProducts();
  }, []);

  const goToProductOverview = (id) => {
    navigate(`/product/${id}`);
  };

  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Casual Shoes</h2>

        <div className="mt-6 grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
          {products.map((product) => (
            <div
              key={product._id}
              className="group relative cursor-pointer"
              onClick={() => goToProductOverview(product._id)}
            >
              <img
                alt={product.name}
                src={product.imageSrc}
                className="aspect-square w-full rounded-md bg-gray-200 object-cover group-hover:opacity-75"
              />
              <div className="mt-4 flex justify-between">
                <div>
                  <h3 className="text-sm text-gray-700">{product.name}</h3>
                </div>
                <p className="text-sm font-medium text-gray-900">{product.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
