import React, { useEffect, useState } from "react";

export default function Sports() {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedSize, setSelectedSize] = useState("");

  // Fetch all sports products
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products/sports");
        if (!response.ok) throw new Error("Failed to fetch products");

        const data = await response.json();
        setProducts(data); // Set fetched products
      } catch (err) {
        console.error("Error fetching products:", err);
      }
    };

    fetchProducts();
  }, []);

  // Open product modal
  const openModal = (product) => {
    setSelectedProduct(product);
    setSelectedSize(product.sizes[0]); // Default size selection
    setIsModalOpen(true);
  };

  // Close product modal
  const closeModal = () => {
    setSelectedProduct(null);
    setIsModalOpen(false);
  };

  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Sports Shoes</h2>

        <div className="mt-6 grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
          {products.map((product) => (
            <div
              key={product._id} // Use MongoDB's _id
              className="group relative cursor-pointer"
              onClick={() => openModal(product)}
            >
              <img
                alt={product.name}
                src={product.imageSrc}
                className="aspect-square w-full rounded-md bg-gray-200 object-cover group-hover:opacity-75"
              />
              <div className="mt-4 flex justify-between">
                <div>
                  <h3 className="text-sm text-gray-700">{product.name}</h3>
                </div>
                <p className="text-sm font-medium text-gray-900">{product.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal to show product details */}
      {isModalOpen && selectedProduct && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-lg p-6 shadow-lg w-full max-w-md relative">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
              onClick={closeModal}
            >
              âœ•
            </button>
            <img
              alt={selectedProduct.name}
              src={selectedProduct.imageSrc}
              className="w-full max-h-80 rounded-lg object-cover"
            />
            <h1 className="text-2xl font-bold tracking-tight text-gray-900 mt-4">
              {selectedProduct.name}
            </h1>
            <p className="text-3xl tracking-tight text-gray-900">{selectedProduct.price}</p>
            <p className="mt-4 text-gray-700">{selectedProduct.description}</p>

            {/* Size Dropdown */}
            <div className="mt-6">
              <h3 className="text-sm font-medium text-gray-900">Size</h3>
              <select
                value={selectedSize}
                onChange={(e) => setSelectedSize(e.target.value)}
                className="mt-2 block w-32 rounded-md border-gray-300 shadow-sm focus:border-indigo-500"
              >
                {selectedProduct.sizes.map((size) => (
                  <option key={size} value={size}>
                    {size}
                  </option>
                ))}
              </select>
            </div>

            {/* Add to Cart Button */}
            <button
              type="button"
              className="mt-10 w-full rounded-md bg-gray-900 px-8 py-3 text-white font-medium hover:bg-gray-800"
            >
              Add to Bag
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
