import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const products = [
  {
    id: 1,
    name: "Nike Zoom Fly 6 'Eliud Kipchoge'",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/951d61fb-6bec-478e-935a-825bcc181795/ZOOM+FLY+6+EK.png",
    price: "₹16,000.00",
    color: "White & Orange",
  },
  {
    id: 2,
    name: "Nike Victory 2 Electric",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/b2de2cc5-d717-4d1e-8028-8233ae6df96e/AIR+ZOOM+VICTORY+2+OLY.png",
    price: "₹17,000.00",
    color: "Orange",
  },
  {
    id: 3,
    name: "Nike Streakfly",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/1bf6f4d9-0b4d-4477-9406-9f6c01250096/NIKE+ZOOMX+STREAKFLY.png",
    price: "₹14,000.00",
    color: "Green",
  },
  {
    id: 4,
    name: "LeBron XXII 'Crown Jewel' EP",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/91a51763-80fe-488e-a5b2-8f9aff4c9794/LEBRON+XXII+EP.png",
    price: "₹16,550.00",
    color: "Mix",
  },
  {
    id: 5,
    name: "Nike Mercurial Vapor 16 Club",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/05442d82-fde1-401f-a24e-33409f65c375/VAPOR+16+CLUB+FG%2FMG.png",
    price: "₹4,000.00",
    color: "Blue",
  },
  {
    id: 6,
    name: "Nike Phantom GX 2 Academy EasyOn Electric",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/cb278f08-a4ad-43b8-824a-641196ddaf4c/PHANTOM+GX+II+ACAD+EASE+FGMG.png",
    price: "₹9,500.00",
    color: "White & Orange",
  },
  {
    id: 7,
    name: "Nike Zoom Mercurial Superfly 9 Elite 'Marcus Rashford'",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/8e644f9b-4db8-4d24-91d3-1edf45ae1e3c/ZOOM+SUPERFLY+9+ELITE+MR+FG.png",
    price: "₹25,000.00",
    color: "White Mix",
  },
  {
    id: 8,
    name: "Nike Mercurial Superfly 10 Academy",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/615bd8bf-ea41-487a-b3e1-70399eef6be6/ZM+SUPERFLY+10+ACAD+FG%2FMG.png",
    price: "₹3,550.00",
    color: "Black",
  },
];

export default function Sports() {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate(); // Used to navigate to Cart

  const openModal = (product) => {
    setSelectedProduct(product);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setSelectedProduct(null);
    setIsModalOpen(false);
  };

  const addToCart = () => {
    const cartItem = { ...selectedProduct, quantity: 1 };

    // Get cart items from localStorage (if any)
    const cartItems = JSON.parse(localStorage.getItem("cartItems")) || [];

    // Add new item to the cart
    cartItems.push(cartItem);

    // Save updated cart items to localStorage
    localStorage.setItem("cartItems", JSON.stringify(cartItems));

    closeModal(); // Close the modal
    navigate("/cart"); // Navigate to the Cart page after adding to the cart
  };

  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Sports Shoes</h2>

        <div className="mt-6 grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
          {products.map((product) => (
            <div
              key={product.id}
              className="group relative cursor-pointer"
              onClick={() => openModal(product)}
            >
              <img
                alt={product.name}
                src={product.imageSrc}
                className="aspect-square w-full rounded-md bg-gray-200 object-cover group-hover:opacity-75 lg:aspect-auto lg:h-80"
              />
              <div className="mt-4 flex justify-between">
                <div>
                  <h3 className="text-sm text-gray-700">{product.name}</h3>
                  <p className="mt-1 text-sm text-gray-500">{product.color}</p>
                </div>
                <p className="text-sm font-medium text-gray-900">{product.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {isModalOpen && selectedProduct && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-lg p-6 shadow-lg w-full max-w-md">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
              onClick={closeModal}
            >
              ✕
            </button>
            <img
              src={selectedProduct.imageSrc}
              alt={selectedProduct.name}
              className="rounded-md w-full h-80 object-cover"
            />
            <h3 className="mt-4 text-lg font-bold text-gray-900">{selectedProduct.name}</h3>
            <p className="text-sm text-gray-500">{selectedProduct.color}</p>
            <p className="mt-2 text-lg font-medium text-gray-900">{selectedProduct.price}</p>
            <div className="mt-6 flex space-x-4">
              <button
                className="add-to-cart-button flex-1 py-2 px-4 text-white"
                onClick={addToCart}
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
