import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { StarIcon } from '@heroicons/react/20/solid';
import { RadioGroup, Radio } from '@headlessui/react';

const products = [
    {
        id: 1,
        name: "Nike Air Force 1'07 Fresh",
        price: "₹11,000.00",
        imageSrc:
            "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/4b24bc11-4ea0-4ee5-8d16-aa58b518475b/AIR+FORCE+1+%2707+FRESH.png",
        description: "You can't stop ageing, but the Air Force 1 'Fresh' gets pretty close. Soft, textured leather helps conceal creasing and is easy to clean. The debossed branding, which replaces the woven labels, pairs with extra laces so you can eat that jam doughnut in peace. And the perforated sockliner keeps it airy and breathable. Now, there's really no reason not to rock white-on-white",
        colors: ["White", "Black", "Red"],
        sizes: ["S", "M", "L", "XL"],
        highlights: ["Ultra-comfortable", "Iconic Design", "Durable"]
    },
    {
        id: 2,
        name: "Nike Air Force 1'07",
        price: "₹7,000.00",
        imageSrc:
            "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/21d38052-598b-44f6-a857-123c9f72b015/AIR+FORCE+1+%2707.png",
        description: "Classic Nike Air Force 1 with timeless appeal.",
        colors: ["Black", "White"],
        sizes: ["M", "L", "XL"],
        highlights: ["Premium Quality", "Soft Leather", "Stylish Design"]
    },
    // Add more products as needed...
];

function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

export default function ProductOverview() {
    const { id } = useParams(); // Get the product ID from the URL
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [selectedColor, setSelectedColor] = useState(""); // Color state
    const [selectedSize, setSelectedSize] = useState(""); // Size state

    useEffect(() => {
        const product = products.find((prod) => prod.id === parseInt(id));
        setSelectedProduct(product);
        if (product) {
            setSelectedColor(product.colors[0]); // Set initial color
            setSelectedSize(product.sizes[0]); // Set initial size
        }
    }, [id]); // Update product when id changes

    if (!selectedProduct) {
        return <div>Loading...</div>;
    }

    return (
        <div className="bg-white">
            <div className="pt-6">
                <nav aria-label="Breadcrumb">
                    <ol role="list" className="mx-auto flex max-w-2xl items-center space-x-2 px-4 sm:px-6 lg:max-w-7xl lg:px-8">
                        <li>
                            <div className="flex items-center">
                                <a href="#" className="mr-2 text-sm font-medium text-gray-900">Home</a>
                                <svg fill="currentColor" width={16} height={20} viewBox="0 0 16 20" aria-hidden="true" className="h-5 w-4 text-gray-300">
                                    <path d="M5.697 4.34L8.98 16.532h1.327L7.025 4.341H5.697z" />
                                </svg>
                            </div>
                        </li>
                        <li className="text-sm">
                            <a href="#" aria-current="page" className="font-medium text-gray-500 hover:text-gray-600">{selectedProduct.name}</a>
                        </li>
                    </ol>
                </nav>

                {/* Product Image */}
                <div className="mx-auto mt-6 max-w-xl sm:px-6 lg:px-8">
                    <img
                        alt={selectedProduct.name}
                        src={selectedProduct.imageSrc}
                        className="w-full max-h-80 rounded-lg object-cover"
                    />
                </div>

                {/* Product Info */}
                <div className="mx-auto max-w-2xl px-4 pb-16 pt-10 sm:px-6 lg:grid lg:max-w-7xl lg:grid-cols-1 lg:gap-x-8 lg:px-8 lg:pb-24 lg:pt-16">
                    <div className="lg:col-span-1">
                        <h1 className="text-2xl font-bold tracking-tight text-gray-900 sm:text-3xl">{selectedProduct.name}</h1>
                        <p className="text-3xl tracking-tight text-gray-900">{selectedProduct.price}</p>
                    </div>

                    {/* Options */}
                    <div className="mt-4">
                        {/* Size Selection */}
                        <div className="mt-10">
                            <h3 className="text-sm font-medium text-gray-900">Size</h3>
                            <select
                                value={selectedSize}
                                onChange={(e) => setSelectedSize(e.target.value)}
                                className="mt-2 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring focus:ring-indigo-500 focus:ring-opacity-50"
                            >
                                {selectedProduct.sizes.map((size) => (
                                    <option key={size} value={size} disabled={!size.inStock}>
                                        {size}
                                    </option>
                                ))}
                            </select>
                        </div>


                        <button
                            type="submit"
                            className="mt-10 flex w-full items-center justify-center rounded-md border border-transparent bg-gray-900 px-8 py-3 text-base font-medium text-white hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-gray-500"
                        >
                            Add to bag
                        </button>
                    </div>

                    {/* Description and details */}
                    <div className="py-10">
                        <h3 className="text-sm font-medium text-gray-900">Description</h3>
                        <p className="text-base text-gray-900">{selectedProduct.description}</p>

                        <div className="mt-10">
                            <h3 className="text-sm font-medium text-gray-900">Highlights</h3>
                            <ul className="list-disc space-y-2 pl-4 text-sm">
                                {selectedProduct.highlights.map((highlight) => (
                                    <li key={highlight} className="text-gray-400">
                                        <span className="text-gray-600">{highlight}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
