import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { RadioGroup, Radio } from '@headlessui/react';

const products = [
  {
    id: 1,
    name: "Nike Air Force 1'07 Fresh",
    href: "#",
    imageSrc:
      "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/4b24bc11-4ea0-4ee5-8d16-aa58b518475b/AIR+FORCE+1+%2707+FRESH.png",
    price: "₹11,000.00",
    color: "White",
    description: "A fresh take on the classic Nike Air Force 1.",
    sizes: [
      { name: "XS", inStock: true },
      { name: "S", inStock: true },
      { name: "M", inStock: false },
      { name: "L", inStock: true },
    ],
  },
  // Add other products here
];

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

export default function Casual() {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedSize, setSelectedSize] = useState(null);
  const navigate = useNavigate();

  const openModal = (product) => {
    setSelectedProduct(product);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setSelectedProduct(null);
    setIsModalOpen(false);
  };

  const addToCart = () => {
    if (!selectedSize) {
      alert("Please select a size.");
      return;
    }
    const cartItem = { ...selectedProduct, quantity: 1, size: selectedSize.name };

    // Get cart items from localStorage (if any)
    const cartItems = JSON.parse(localStorage.getItem("cartItems")) || [];

    // Add new item to the cart
    cartItems.push(cartItem);

    // Save updated cart items to localStorage
    localStorage.setItem("cartItems", JSON.stringify(cartItems));

    closeModal(); // Close the modal
    navigate("/cart"); // Navigate to the Cart page after adding to the cart
  };

  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">Casual Shoes</h2>

        <div className="mt-6 grid grid-cols-1 gap-x-6 gap-y-10 sm:grid-cols-2 lg:grid-cols-4 xl:gap-x-8">
          {products.map((product) => (
            <div
              key={product.id}
              className="group relative cursor-pointer"
              onClick={() => openModal(product)}
            >
              <img
                alt={product.name}
                src={product.imageSrc}
                className="aspect-square w-full rounded-md bg-gray-200 object-cover group-hover:opacity-75 lg:aspect-auto lg:h-80"
              />
              <div className="mt-4 flex justify-between">
                <div>
                  <h3 className="text-sm text-gray-700">{product.name}</h3>
                  <p className="mt-1 text-sm text-gray-500">{product.color}</p>
                </div>
                <p className="text-sm font-medium text-gray-900">{product.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {isModalOpen && selectedProduct && (
        <div className="fixed inset-0 z-[9999] flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-lg p-6 shadow-lg w-full max-w-2xl relative z-[10000]">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
              onClick={closeModal}
            >
              ✕
            </button>

            {/* Product Image and Details */}
            <div className="lg:grid lg:grid-cols-3 lg:gap-x-8">
              <div className="lg:col-span-1">
                <img
                  alt={selectedProduct.name}
                  src={selectedProduct.imageSrc}
                  className="rounded-lg w-full h-full object-cover"
                />
              </div>

              <div className="lg:col-span-2 lg:pl-8">
                <h3 className="text-lg font-bold text-gray-900">{selectedProduct.name}</h3>
                <p className="text-gray-600">{selectedProduct.color}</p>
                <p className="mt-4 text-xl text-gray-900">{selectedProduct.price}</p>

                <div className="mt-4">
                  {/* Size Selection */}
                  <h4 className="text-sm font-medium text-gray-900">Size</h4>
                  <RadioGroup value={selectedSize} onChange={setSelectedSize} className="mt-2">
                    {selectedProduct.sizes.map((size) => (
                      <Radio
                        key={size.name}
                        value={size}
                        disabled={!size.inStock}
                        className={classNames(
                          size.inStock ? 'cursor-pointer bg-white text-gray-900 shadow-sm' : 'cursor-not-allowed bg-gray-50 text-gray-200',
                          'group relative flex items-center justify-center rounded-md border px-4 py-3 text-sm font-medium uppercase hover:bg-gray-50'
                        )}
                      >
                        {size.name}
                      </Radio>
                    ))}
                  </RadioGroup>
                </div>

                {/* Description */}
                <p className="mt-6 text-sm text-gray-500">{selectedProduct.description}</p>

                {/* Add to Cart Button */}
                <div className="mt-8">
                  <button
                    type="button"
                    onClick={addToCart}
                    className="w-full py-3 px-6 bg-indigo-600 text-white text-sm font-semibold rounded-md hover:bg-indigo-700 focus:outline-none"
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
