import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { StarIcon } from '@heroicons/react/20/solid';
import { RadioGroup, Radio } from '@headlessui/react';

const products = [
    {
        id: 1,
        name: "Nike Air Force 1'07 Fresh",
        price: "₹11,000.00",
        imageSrc:
            "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/4b24bc11-4ea0-4ee5-8d16-aa58b518475b/AIR+FORCE+1+%2707+FRESH.png",
        description: "A fresh take on the classic Nike Air Force 1.",
        colors: ["White", "Black", "Red"],
        sizes: ["S", "M", "L", "XL"],
        highlights: ["Ultra-comfortable", "Iconic Design", "Durable"]
    },
    {
        id: 2,
        name: "Nike Air Force 1'07",
        price: "₹7,000.00",
        imageSrc:
            "https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/21d38052-598b-44f6-a857-123c9f72b015/AIR+FORCE+1+%2707.png",
        description: "Classic Nike Air Force 1 with timeless appeal.",
        colors: ["Black", "White"],
        sizes: ["M", "L", "XL"],
        highlights: ["Premium Quality", "Soft Leather", "Stylish Design"]
    },
    // Add more products as needed...
];

function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

export default function ProductOverview() {
    const { id } = useParams(); // Get the product ID from the URL
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [selectedColor, setSelectedColor] = useState(""); // Color state
    const [selectedSize, setSelectedSize] = useState(""); // Size state

    useEffect(() => {
        const product = products.find((prod) => prod.id === parseInt(id));
        setSelectedProduct(product);
        if (product) {
            setSelectedColor(product.colors[0]); // Set initial color
            setSelectedSize(product.sizes[0]); // Set initial size
        }
    }, [id]); // Update product when id changes

    if (!selectedProduct) {
        return <div>Loading...</div>;
    }

    return (
        <div className="bg-white">
            <div className="pt-6">
                <nav aria-label="Breadcrumb">
                    <ol role="list" className="mx-auto flex max-w-2xl items-center space-x-2 px-4 sm:px-6 lg:max-w-7xl lg:px-8">
                        <li>
                            <div className="flex items-center">
                                <a href="#" className="mr-2 text-sm font-medium text-gray-900">Home</a>
                                <svg fill="currentColor" width={16} height={20} viewBox="0 0 16 20" aria-hidden="true" className="h-5 w-4 text-gray-300">
                                    <path d="M5.697 4.34L8.98 16.532h1.327L7.025 4.341H5.697z" />
                                </svg>
                            </div>
                        </li>
                        <li className="text-sm">
                            <a href="#" aria-current="page" className="font-medium text-gray-500 hover:text-gray-600">{selectedProduct.name}</a>
                        </li>
                    </ol>
                </nav>

                {/* Image gallery */}
                <div className="mx-auto mt-6 max-w-2xl sm:px-6 lg:grid lg:max-w-7xl lg:grid-cols-3 lg:gap-x-8 lg:px-8">
                    <img
                        alt={selectedProduct.name}
                        src={selectedProduct.imageSrc}
                        className="hidden aspect-[21/10] size-full rounded-lg object-cover lg:block"
                    />
                </div>

                {/* Product info */}
                <div className="mx-auto max-w-2xl px-4 pb-16 pt-10 sm:px-6 lg:grid lg:max-w-7xl lg:grid-cols-3 lg:grid-rows-[auto_auto_1fr] lg:gap-x-8 lg:px-8 lg:pb-24 lg:pt-16">
                    <div className="lg:col-span-2 lg:border-r lg:border-gray-200 lg:pr-8">
                        <h1 className="text-2xl font-bold tracking-tight text-gray-900 sm:text-3xl">{selectedProduct.name}</h1>
                    </div>

                    {/* Options */}
                    <div className="mt-4 lg:row-span-3 lg:mt-0">
                        <p className="text-3xl tracking-tight text-gray-900">{selectedProduct.price}</p>

                        {/* Reviews */}
                        <div className="mt-6">
                            <div className="flex items-center">
                                <div className="flex items-center">
                                    {[0, 1, 2, 3, 4].map((rating) => (
                                        <StarIcon
                                            key={rating}
                                            aria-hidden="true"
                                            className={classNames(
                                                4 > rating ? 'text-gray-900' : 'text-gray-200',
                                                'size-5 shrink-0'
                                            )}
                                        />
                                    ))}
                                </div>
                                <a href="#" className="ml-3 text-sm font-medium text-indigo-600 hover:text-indigo-500">117 reviews</a>
                            </div>
                        </div>

                        {/* Color Selection */}
                        <div>
                            <h3 className="text-sm font-medium text-gray-900">Color</h3>
                            <RadioGroup value={selectedColor} onChange={setSelectedColor} className="flex items-center gap-x-3">
                                {selectedProduct.colors.map((color) => (
                                    <Radio key={color} value={color} aria-label={color} className="relative flex cursor-pointer items-center justify-center rounded-full p-0.5">
                                        <span aria-hidden="true" className={`bg-${color.toLowerCase()} rounded-full`}></span>
                                    </Radio>
                                ))}
                            </RadioGroup>
                        </div>

                        {/* Size Selection */}
                        <div className="mt-10">
                            <h3 className="text-sm font-medium text-gray-900">Size</h3>
                            <RadioGroup value={selectedSize} onChange={setSelectedSize} className="grid grid-cols-4 gap-4 sm:grid-cols-8 lg:grid-cols-4">
                                {selectedProduct.sizes.map((size) => (
                                    <Radio key={size} value={size} disabled={!size.inStock} className="group relative flex items-center justify-center rounded-md border px-4 py-3 text-sm font-medium uppercase">
                                        <span>{size.name}</span>
                                    </Radio>
                                ))}
                            </RadioGroup>
                        </div>

                        <button
                            type="submit"
                            className="mt-10 flex w-full items-center justify-center rounded-md border border-transparent bg-indigo-600 px-8 py-3 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        >
                            Add to bag
                        </button>
                    </div>

                    {/* Description and details */}
                    <div className="py-10 lg:col-span-2 lg:col-start-1 lg:border-r lg:border-gray-200 lg:pb-16 lg:pr-8 lg:pt-6">
                        <h3 className="text-sm font-medium text-gray-900">Description</h3>
                        <p className="text-base text-gray-900">{selectedProduct.description}</p>

                        <div className="mt-10">
                            <h3 className="text-sm font-medium text-gray-900">Highlights</h3>
                            <ul className="list-disc space-y-2 pl-4 text-sm">
                                {selectedProduct.highlights.map((highlight) => (
                                    <li key={highlight} className="text-gray-400">
                                        <span className="text-gray-600">{highlight}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>

                        <div className="mt-10">
                            <h2 className="text-sm font-medium text-gray-900">Details</h2>
                            <p className="text-sm text-gray-600">{selectedProduct.details}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
