import React from "react";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";

function Home() {
  const navigate = useNavigate();

  const handleButtonClick = () => {
    navigate("/products");
  };

  return (
    <div className="flex flex-col min-h-screen"> 
    <main className="flex-grow">
      <section className="welcome-section">
        {/*Gradient Top*/}
        <div
          aria-hidden="true"
          className="absolute inset-x-0 -top-40 -z-10 transform-gpu overflow-hidden blur-3xl sm:-top-80"
        >
          <div
            style={{
              clipPath:
                'polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)',
            }}
            className="relative left-[calc(50%-11rem)] aspect-[1155/678] w-[36.125rem] -translate-x-1/2 rotate-[30deg] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%-30rem)] sm:w-[72.1875rem]"
          />
        </div>

        {/*Main Section*/}
        <h1>Welcome to Our Store</h1>
        <p>Discover the best shoes and enjoy a seamless shopping experience.</p>
        <button onClick={handleButtonClick}>Shop Now</button>
      </section>

      {/*Gradient Bottom*/}
      <div
        aria-hidden="true"
        className="absolute inset-x-0 top-[calc(100%-13rem)] -z-10 transform-gpu overflow-hidden blur-3xl sm:top-[calc(100%-30rem)]"
      >
        <div
          style={{
            clipPath:
              'polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)',
          }}
          className="relative left-[calc(50%+3rem)] aspect-[1155/678] w-[36.125rem] -translate-x-1/2 bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%+36rem)] sm:w-[72.1875rem]"
        />
      </div>

      <footer className="bg-transparent text-black py-4 mt-auto w-full">
      <div className="max-w-screen-xl mx-auto px-4 text-center">
        <div className="flex justify-center space-x-6 mb-4">
          <a href="#" className="hover:text-gray-700 text-black">About</a>
          <a href="#" className="hover:text-gray-700 text-black">Privacy Policy</a>
          <a href="#" className="hover:text-gray-700 text-black">Licensing</a>
          <a href="#" className="hover:text-gray-700 text-black">Contact</a>
        </div>
        <hr className="my-4 border-gray-500" />
        <span className="text-sm text-black">
          © 2024 <a href="https://flowbite.com/" className="hover:underline text-black">Flowbite™</a>. All Rights Reserved.
        </span>
      </div>
    </footer>

    </main>
    </div>
  );
}

export default Home;
