'use client'

import { useNavigate } from 'react-router-dom';
import logo from './assets/logo1.png';
import { Fragment, useState, useEffect } from 'react';
import {
  Dialog,
  DialogBackdrop,
  DialogPanel,
  Popover,
  PopoverButton,
  PopoverGroup,
  PopoverPanel,
  Tab,
  TabGroup,
  TabList,
  TabPanel,
  TabPanels,
} from '@headlessui/react'
import { Bars3Icon, MagnifyingGlassIcon, ShoppingBagIcon, XMarkIcon } from '@heroicons/react/24/outline'

const navigation = {
  categories: [
    {
      id: 'product',
      name: 'Products',
      featured: [
        {
          name: 'Jordans',
          href: '/designer',
          imageSrc: 'https://5.imimg.com/data5/ANDROID/Default/2021/12/RS/LO/UU/19051907/product-jpeg.jpg',
          imageAlt: 'Nike1',
        },
        {
          name: 'Casual Shoes',
          href: '/casual',
          imageSrc: 'https://static.nike.com/a/images/t_PDP_936_v1/f_auto,q_auto:eco/f14bd267-64ea-4cb0-903d-01760b32b9eb/W+NIKE+V2K+RUN.png',
          imageAlt: 'Nike2',
        },
      ],
      sections: [
        {
          id: 'shoes',
          name: 'Shoes',
          items: [
            { name: 'Casual', href: '/casual' },
            { name: 'Skateboarding', href: '/skateboarding' },
            { name: 'Designer', href: '/designer' },
            { name: 'Sports', href: '/sports' },
          ],
        },
        {
          id: 'jordans',
          name: 'Jordans',
          items: [
            { name: 'Jordan 1s', href: '/designer' },
            { name: 'Jordan 4s', href: '/designer' },
          ],
        },
      ],
    },
  ],
  pages: [
    { name: 'Products', href: '/products' },
    { name: 'Home', href: '/' },
    { name: 'About Us', href: '/aboutus' },
  ],
}

export default function Header() {
  const [open, setOpen] = useState(false);
  const navigate = useNavigate(); // Initialize navigate function

  // Check if user is logged in (i.e., if a JWT token exists in localStorage)
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token"); // Get the token from localStorage
    if (token) {
      setIsLoggedIn(true); // If token exists, the user is logged in
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token from localStorage
    setIsLoggedIn(false); // Update state
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className="bg-white">
      <header className="relative bg-white">
        <nav aria-label="Top" className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
          <div className="border-b border-gray-200">
            <div className="flex h-16 items-center">

              <div className="ml-4 flex lg:ml-0">
                <a href="/">
                  <span className="sr-only">FootFolio</span>
                  <img src={logo} alt="Logo" className="logo-img" />
                </a>
              </div>

              {/* Flyout menus */}
              <PopoverGroup className="hidden lg:ml-8 lg:block lg:self-stretch">
                <div className="flex h-full space-x-8">
                  {navigation.categories.map((category) => (
                    <Popover key={category.name} className="flex">
                      <div className="relative flex">
                        <PopoverButton className="relative z-10 -mb-px flex items-center border-b-2 border-transparent pt-px text-sm font-medium text-gray-700 transition-colors duration-200 ease-out hover:text-gray-800 data-[open]:border-gray-600 data-[open]:text-gray-600">
                          {category.name}
                        </PopoverButton>
                      </div>

                      <PopoverPanel
                        transition
                        className="absolute inset-x-0 top-full text-sm text-gray-500 transition data-[closed]:opacity-0 data-[enter]:duration-200 data-[leave]:duration-150 data-[enter]:ease-out data-[leave]:ease-in"
                      >
                        <div aria-hidden="true" className="absolute inset-0 top-1/2 bg-white shadow" />

                        <div className="relative bg-white">
                          <div className="mx-auto max-w-7xl px-8">
                            <div className="grid grid-cols-2 gap-x-8 gap-y-10 py-16">
                              <div className="col-start-2 grid grid-cols-2 gap-x-8">
                                {category.featured.map((item) => (
                                  <div key={item.name} className="group relative text-base sm:text-sm">
                                    <img
                                      alt={item.imageAlt}
                                      src={item.imageSrc}
                                      className="aspect-square w-full rounded-lg bg-gray-100 object-cover group-hover:opacity-75"
                                    />
                                    <a href={item.href} className="mt-6 block font-medium text-gray-900">
                                      <span aria-hidden="true" className="absolute inset-0 z-10" />
                                      {item.name}
                                    </a>
                                    <p aria-hidden="true" className="mt-1">
                                      Shop now
                                    </p>
                                  </div>
                                ))}
                              </div>
                              <div className="row-start-1 grid grid-cols-3 gap-x-8 gap-y-10 text-sm">
                                {category.sections.map((section) => (
                                  <div key={section.name}>
                                    <p id={`${section.name}-heading`} className="font-medium text-gray-900">
                                      {section.name}
                                    </p>
                                    <ul
                                      role="list"
                                      aria-labelledby={`${section.name}-heading`}
                                      className="mt-6 space-y-6 sm:mt-4 sm:space-y-4"
                                    >
                                      {section.items.map((item) => (
                                        <li key={item.name} className="flex">
                                          <a href={item.href} className="hover:text-gray-800">
                                            {item.name}
                                          </a>
                                        </li>
                                      ))}
                                    </ul>
                                  </div>
                                ))}
                              </div>
                            </div>
                          </div>
                        </div>
                      </PopoverPanel>
                    </Popover>
                  ))}

                  {navigation.pages.map((page) => (
                    <a
                      key={page.name}
                      href={page.href}
                      className="flex items-center text-sm font-medium text-gray-700 hover:text-gray-800"
                    >
                      {page.name}
                    </a>
                  ))}
                </div>
              </PopoverGroup>

              <div className="ml-auto flex items-center">
                <div className="hidden lg:flex lg:flex-1 lg:items-center lg:justify-end lg:space-x-6">
                  {isLoggedIn ? (
                    <>
                      <button
                        onClick={handleLogout}
                        className="text-sm font-medium text-gray-700 hover:text-gray-800"
                      >
                        Logout
                      </button>
                      {/* Cart Icon */}
                      <div className="ml-4 flow-root lg:ml-6">
                        <a href="/cart" className="group -m-2 flex items-center p-2">
                          <ShoppingBagIcon
                            aria-hidden="true"
                            className="size-6 shrink-0 text-gray-400 group-hover:text-gray-500"
                          />
                          <span className="sr-only">items in cart, view bag</span>
                        </a>
                      </div>
                    </>
                  ) : (
                    <>
                      <a href="/login" className="text-sm font-medium text-gray-700 hover:text-gray-800">
                        Log in
                      </a>
                      <span aria-hidden="true" className="h-6 w-px bg-gray-200" />
                      <a href="/register" className="text-sm font-medium text-gray-700 hover:text-gray-800">
                        Create account
                      </a>
                    </>
                  )}
                </div>
              </div>
            </div>
          </div>
        </nav>
      </header>
    </div>
  );
}
