'use client';

import { useNavigate } from 'react-router-dom';
import logo from './assets/logo1.png';
import { Fragment, useState, useEffect } from 'react';
import { Bars3Icon, MagnifyingGlassIcon, ShoppingBagIcon, XMarkIcon } from '@heroicons/react/24/outline';

const navigation = {
  pages: [
    { name: 'Products', href: '/products' },
    { name: 'Home', href: '/' },
    { name: 'About Us', href: '/aboutus' },
  ],
};

export default function Header() {
  const [open, setOpen] = useState(false);
  const navigate = useNavigate(); // Initialize navigate function

  // Check if user is logged in (i.e., if a JWT token exists in localStorage)
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token"); // Get the token from localStorage
    if (token) {
      setIsLoggedIn(true); // If token exists, the user is logged in
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token from localStorage
    setIsLoggedIn(false); // Update state
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className="bg-white">
      <header className="relative bg-white">
        <nav aria-label="Top" className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
          <div className="border-b border-gray-200">
            <div className="flex h-16 items-center">
              <button
                type="button"
                onClick={() => setOpen(true)}
                className="relative rounded-md bg-white p-2 text-gray-400 lg:hidden"
              >
                <span className="absolute -inset-0.5" />
                <span className="sr-only">Open menu</span>
                <Bars3Icon aria-hidden="true" className="size-6" />
              </button>

              <div className="ml-4 flex lg:ml-0">
                <a href="/">
                  <span className="sr-only">Your Company</span>
                  <img src={logo} alt="Logo" className="logo-img" />
                </a>
              </div>

              <div className="ml-auto flex items-center">
                <div className="hidden lg:flex lg:flex-1 lg:items-center lg:justify-end lg:space-x-6">
                  {isLoggedIn ? (
                    <>
                      <button
                        onClick={handleLogout}
                        className="text-sm font-medium text-gray-700 hover:text-gray-800"
                      >
                        Logout
                      </button>
                      {/* Cart Icon */}
                      <div className="ml-4 flow-root lg:ml-6">
                        <a href="/cart" className="group -m-2 flex items-center p-2">
                          <ShoppingBagIcon
                            aria-hidden="true"
                            className="size-6 shrink-0 text-gray-400 group-hover:text-gray-500"
                          />
                          <span className="sr-only">items in cart, view bag</span>
                        </a>
                      </div>
                    </>
                  ) : (
                    <>
                      <a href="/login" className="text-sm font-medium text-gray-700 hover:text-gray-800">
                        Log in
                      </a>
                      <span aria-hidden="true" className="h-6 w-px bg-gray-200" />
                      <a href="/register" className="text-sm font-medium text-gray-700 hover:text-gray-800">
                        Create account
                      </a>
                    </>
                  )}
                </div>
              </div>
            </div>
          </div>
        </nav>
      </header>
    </div>
  );
}
